import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class WebSocketConnection extends Component {
  constructor(props) {
    super(props);

    this.connection = new WebSocket('ws://127.0.0.1:1337');
    this.connection.onopen = this.onOpen;
    this.connection.onerror = this.onError;
    this.connection.onmessage = this.onMessage.bind(this);

    this.state = {
      socketMessage: { data: '' }
    };

  }
  onOpen() {
    console.log('Connection is opened...');
  }
  onError(error) {
    console.log('Connection error occured!', error);
  }
  onMessage(message) {
    console.log('message top level', message);
    this.setState({socketMessage: message});
    /* 
    try {
      var json = JSON.parse(message.data);
    } catch (e) {
      console.log('Invalid JSON: ', message.data);
      return;
    }

    if (json.type === 'color') { 
      myColor = json.data;
      status.text(myName + ': ').css('color', myColor);
      input.removeAttr('disabled').focus();
      // from now user can start sending messages
    } else if (json.type === 'history') { // entire message history
      // insert every single message to the chat window
      for (var i=0; i < json.data.length; i++) {
      addMessage(json.data[i].author, json.data[i].text,
          json.data[i].color, new Date(json.data[i].time));
      }
    } else if (json.type === 'message') { // it's a single message
      // let the user write another message
      input.removeAttr('disabled'); 
      addMessage(json.data.author, json.data.text,
                 json.data.color, new Date(json.data.time));
    } else {
      console.log('Untyped JSON message:', json);
    }
    */
  }
  render() {
    return (
      <div>

      <Chat 
        connection={this.connection} 
        onMessage={this.onMessage}
        socketMessage={this.state.socketMessage.data}
      />
      </div>


    );
   }
}

class Chat extends Component {
  constructor(props) {
    super(props);

    this.handleKeyPress = this.handleKeyPress.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.onMessage = this.onMessage.bind(this);

    this.state = {
      chatMessage: '',
      disabled: '',
      chatName: false
    };
    this.input = React.createRef();
  }

  handleKeyPress(e) {
    if (e.key === 'Enter') {
      let chatMessage = this.state.chatMessage;
      if (!chatMessage) {
        return;
      }

      this.props.connection.send(chatMessage);
      this.setState({
        chatMessage: '',
        disabled: 'disabled',
      });

      // we know that the first message sent from a user their name  
      if (this.state.chatName === false) {
        this.setState({ chatName: chatMessage });
      }
    }
  }
  handleChange(e) {
    this.setState({chatMessage: e.target.value});
  }
  onMessage(message) {
    console.log('onMessage in Chat');
  }
  socketMessageObject() {
    try {
      return JSON.parse(this.props.socketMessage);
    } catch (e) {
      console.log('Invalid JSON: ', this.props.socketMessage);
      return false;
    }
  }
  isConnected() {
    return !!this.socketMessageObject();
  }
  userName() {
    return his.socketMessageObject().userName;
  }
  statusText() {
    if (this.userName().length > 0) {
      return this.userName();
    } else {
      return 'Enter name:';
    }
  }
  statusColor() {
    if (this.socketMessageObject().type === 'color') {
      return this.socketMessageObject().data;
    } else {
      return false;
    }
  }
  render() {
    return (
      <div>
        !!!this.props.socketMessage: {this.props.socketMessage}
        <div id="content"></div>
        <div>
          <span 
            id="status" 
            style={{ 
              color: this.statusColor() 
          }}>
            {this.statusText()}
          </span>
          <input 
            type="text" 
            id="input" 
            value={this.state.chatMessage}
            disabled={this.state.disabled}
            onKeyPress={this.handleKeyPress}
            onChange={this.handleChange}
          />
        </div>
      </div>
    );
  }
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { counter: 0 };
  }

  render() {
    return (
      <div>
        <WebSocketConnection onMessage={this.onMessage} socketMessage={this.state.socketMessage}/>
      </div>
    );
  }
}

export default App;
